//Fit, crop 적용
@Preview
@Composable
private fun ContentScaleAsyncImageExample() {
    Column {
        AsyncImage(
            model = "data:image/jpeg;base64,",
            contentDescription = null,
            contentScale = ContentScale.Fit,
            modifier = Modifier
                .size(100.dp)
        )
        AsyncImage(
            model = "data:image/jpeg;base64,",
            contentDescription = null,
            contentScale = ContentScale.Crop,
            modifier = Modifier
                .size(100.dp)
        )
        AsyncImage(
            model = "data:image/jpeg;base64,",
            contentDescription = null,
            contentScale = ContentScale.FillBounds, //가로세로 다채움
            modifier = Modifier
                .size(100.dp)
        )
        AsyncImage(
            model = "data:image/jpeg;base64,",
            contentDescription = null,
            contentScale = ContentScale.Crop,
            modifier = Modifier
                .size(100.dp)
                .clip(CircleShape)
        )
    }
}